package javafasttrack01;

public class BangCuuChuong {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i = 1; i <= 10; i++) {
			System.out.println("");
			for(int j = 2; j <= 9; j++) {				
				System.out.print(j+" x "+i+" = "+(i*j)+"\t");				
			}
		}
	}

}
